//该文件用于创建Vuex中最为核心的store
//引入vuex
import Vue from "vue"
//引入vuex
import vuex from "vuex"

//应用插件
Vue.use(vuex)
//准备actions，用于响应组件中的动作
const actions = {
    add(context, value) {
        context.commit("ADD", value)
    }
}
//准备motations，用于操作数据（state）
const mutations = {
    ADD(state, value) {
        state += value
    }
}
//准备satte，用于储存数据
const state = {
    sum: 0  //当前求和的数
}

//创建并暴露store接口
export default new Vuex.Store({
    actions,mutations,state
});
