export default {
  name: "About",
  //通过路由规则，进入该组件时被调用
  beforeRouteEnter: function beforeRouteEnter(to, from, next) {
    if (to.meta.isAuth) {
      //判断是否需要权限
      if (localStorage.getItem("school") === "atguigu") {
        next();
      } else alert("无权限访问！");
    } else {
      next();
    }
  },
  //通过路由规则，离开该组件时被调用
  beforeRouteLeave: function beforeRouteLeave(to, from, next) {
    console.log("离开组件");
    next();
  }
};
